import React, { useState } from "react";
import axios from "axios";
import SearchBar from "./components/SearchBar";
import SearchResults from "./components/SearchResults";
import "./index.css";

const App = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [filter, setFilter] = useState("all");

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/search?query=${query}`
      );
      setResults(response.data);
    } catch (error) {
      console.error("Error fetching search results:", error);
    }
  };

  const filteredResults = results.filter(
    (result) => filter === "all" || result.type === filter
  );

  return (
    <div className="App">
      <h1>Search Engine</h1>
      <SearchBar
        query={query}
        setQuery={setQuery}
        handleSearch={handleSearch}
        setFilter={setFilter}
      />
      <div className="filter-options">
        <button onClick={() => setFilter("all")}>All</button>
        <button onClick={() => setFilter("video")}>Videos</button>
        <button onClick={() => setFilter("article")}>Articles</button>
        <button onClick={() => setFilter("book")}>Books</button>
        <button onClick={() => setFilter("post")}>Posts</button>
        <button onClick={() => setFilter("academic")}>Academic</button>
      </div>
      <SearchResults results={filteredResults} />
    </div>
  );
};

export default App;
