import React, { useState } from "react";
import "./SearchResults.css";

const SearchResults = ({ results }) => {
  const [filter, setFilter] = useState("all");

  const handleFilterChange = (type) => {
    setFilter(type);
  };

  const filteredResults = results.filter(
    (result) => filter === "all" || result.type === filter
  );

  return (
    <div>
      <div className="filter-buttons">
        <button onClick={() => handleFilterChange("all")}>All</button>
        <button onClick={() => handleFilterChange("youtube")}>YouTube</button>
        <button onClick={() => handleFilterChange("article")}>Articles</button>
        <button onClick={() => handleFilterChange("book")}>Books</button>
        <button onClick={() => handleFilterChange("reddit")}>Blogs</button>
        <button onClick={() => handleFilterChange("academic")}>
          Academic Papers
        </button>
      </div>

      <div className="results-container">
        {filteredResults.length > 0 ? (
          filteredResults.map((result, index) => (
            <div key={index} className={`result-card ${result.type}`}>
              <h3>{result.title}</h3>
              <a href={result.link} target="_blank" rel="noopener noreferrer">
                {result.link}
              </a>
            </div>
          ))
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
};

export default SearchResults;
